import * as React from "react";
import { XGrid } from "@material-ui/x-grid";
import { useDemoData } from "@material-ui/x-grid-data-generator";
// import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import {
  GridToolbarContainer,
  GridToolbar,
  DensitySelector,
  FilterToolbarButton,
  FilterModel
} from "@material-ui/x-grid";

export default function LocationTable() {
  const { data } = useDemoData({
    dataSet: "Commodity",
    rowLength: 200,
    maxColumns: 6
  });
  const useStyles = makeStyles({
    toolbarContainer: {
      padding: 10,
      display: "flex",
      flexDirection: "column"
    },
    title: {
      display: "flex",
      justifyContent: "space-around"
    }
  });
  const riceFilterModel: FilterModel = {
    items: [
      { columnField: "commodity", operatorValue: "contains", value: "rice" }
    ]
  };
  const classes = useStyles();

  function CustomToolbar() {
    return (
      <GridToolbarContainer className={classes.toolbarContainer}>
        <div className={classes.title}>
          <Typography variant="h5" gutterBottom>
            Места производства
          </Typography>
        </div>
        <GridToolbar />
      </GridToolbarContainer>
    );
  }
  return (
    <div style={{ display: "flex", height: 600, width: "100%" }}>
      <div style={{ flexGrow: 1 }}>
        <XGrid
          localeText={{
            columnMenuSortAsc: "По возрастанию",
            columnMenuSortDesc: "По убыванию",
            columnMenuFilter: "Фильтр",
            columnMenuHideColumn: "Скрыть",
            columnMenuShowColumns: "Показать колонки",
            columnMenuUnsort: "Не сортировать"
          }}
          loading={data.rows.length === 0}
          rowHeight={38}
          pageSize={20}
          headerHeight={80}
          columnBuffer={2}
          rowsPerPageOptions={[5, 10, 20, 50, 100]}
          pagination
          density="standard"
          {...data}
          components={{
            Toolbar: CustomToolbar
          }}
        />
      </div>
    </div>
  );
}
