import * as React from "react";
import { XGrid } from "@material-ui/x-grid";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import { GridToolbarContainer, GridToolbar } from "@material-ui/x-grid";
import IconButton from "@material-ui/core/IconButton";
import DeleteForeverOutlinedIcon from "@material-ui/icons/DeleteForeverOutlined";
import EditOutlinedIcon from "@material-ui/icons/EditOutlined";
import russian from "../constants/localeTextConstants.js";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import Tooltip from "@material-ui/core/Tooltip";
import {
  createMuiTheme,
  responsiveFontSizes,
  ThemeProvider
} from "@material-ui/core/styles";

export default function LocationTable({ locations }) {
  console.log(locations);
  const theme = createMuiTheme({
    typography: {
      // In Chinese and Japanese the characters are usually larger,
      // so a smaller fontsize may be appropriate.
      fontSize: 18
    }
  });
  const useStyles = makeStyles({
    toolbarContainer: {
      display: "flex",
      flexDirection: "column"
    },
    tools: {
      width: "100%",
      paddingLeft: 20,
      paddingRight: 20,
      marginBottom: 30,
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-between"
    },
    iconButton: {
      "&:hover": {
        backgroundColor: "#bdbdbd"
      }
    }
  });

  const classes = useStyles();

  function updateRow(params) {
    console.log("updateRow", params);
  }
  function deleteRow(params) {
    console.log("deleteRow", params);
  }

  const editColumn = {
    field: "edit",
    headerName: "Редактировать",
    sortable: false,

    width: 135,
    renderCell: (params: CellParams) => (
      <IconButton
        aria-label="edit"
        color="primary"
        className={classes.iconButton}
        onClick={() => updateRow(params)}
      >
        <EditOutlinedIcon />
      </IconButton>
    )
  };

  const deleteColumn = {
    field: "delete",
    headerName: "Удалить",
    sortable: false,
    renderCell: (params: CellParams) => (
      <IconButton
        aria-label="delete"
        color="secondary"
        className={classes.iconButton}
        onClick={() => deleteRow(params)}
      >
        <DeleteForeverOutlinedIcon />
      </IconButton>
    )
  };

  if (locations.columns && locations.columns.length > 0) {
    locations.columns.push(editColumn);
    locations.columns.push(deleteColumn);
  }

  const columns: ColDef[] = locations.columns ? locations.columns : [];
  const rows = locations.rows ? locations.rows : [];

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  function CustomToolbar() {
    return (
      <GridToolbarContainer className={classes.toolbarContainer}>
        <div>
          <Typography variant="h5" gutterBottom>
            Места производства
          </Typography>
        </div>
        <div className={classes.tools}>
          <div>
            <GridToolbar />
          </div>
          <Tooltip title="Создать">
            <Fab
              size="medium"
              color="primary"
              aria-label="add"
              className={classes.add}
              onClick={handleClickOpen}
            >
              <AddIcon />
            </Fab>
          </Tooltip>
        </div>
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
          <DialogContent>
            <DialogContentText>
              To subscribe to this website, please enter your email address
              here. We will send updates occasionally.
            </DialogContentText>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Email Address"
              type="email"
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={handleClose} color="primary">
              Subscribe
            </Button>
          </DialogActions>
        </Dialog>
      </GridToolbarContainer>
    );
  }
  return (
    <div
      style={{
        display: "flex",
        height: 700,
        width: "100%",
        justifyContent: "space-between",
        flexGrow: 1,
        fontSize: "18px"
      }}
    >
      <ThemeProvider theme={theme}>
        <XGrid
          localeText={russian}
          rowHeight={50}
          pageSize={20}
          headerHeight={80}
          columnBuffer={2}
          rowsPerPageOptions={[5, 10, 20, 50, 100]}
          pagination
          density="standard"
          rows={rows}
          columns={columns}
          disableColumnMenu={true}
          showColumnRightBorder={true}
          showCellRightBorder={true}
          disableExtendRowFullWidth={true}
          components={{
            Toolbar: CustomToolbar
          }}
        />
      </ThemeProvider>
    </div>
  );
}
