import React from "react";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";

export default function SelectTextField({ items, getItem, title, needId }) {
  const [item, setItem] = React.useState("");

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    console.log("onCange");
    setItem(event.target.value);
    getItem(event.target.value);
  };

  const showNotification = text => {
    enqueueSnackbar(text, {
      variant: "warning",
      anchorOrigin: {
        vertical: "top",
        horizontal: "center"
      }
    });
  };

  return (
    <TextField
      id="standard-select-currency"
      select
      label={title}
      value={item}
      onChange={handleChange}
      onClick={() => {
        console.log(items.length);
        if (items.length === 0)
          showNotification(
            `Чтобы выбрать ${title}, необходимо заранее создать их в соответствующем списке.`
          );
      }}
    >
      {items.map(option => (
        <MenuItem key={option.id} value={needId ? option.id : option.name}>
          {option.name}
        </MenuItem>
      ))}
    </TextField>
  );
}
