          obj.field = item;
          obj.flex = 0.1;
          break;
        case "number":
          obj.headerName = "Номер";
          obj.field = item;
          break;
        case "createdAt":
          console.log({ item });
          obj.headerName = "Дата создания";
          obj.field = moment(item, "DD.MM.YYYY hh:mm:ss");
          obj.flex = 0.1;
          break;
        case "updatedAt":
          obj.headerName = "Дата редактирования";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "note":
          obj.headerName = "Примечание";
          obj.field = item;
          obj.flex = 0.1;
          break;
      }
      if (Object.keys(obj).length > 0) {
