import { useState, useEffect } from "react";
import LocationTable from "../components/LocationTable";

const Location = () => {
  const [showAddLocation, setShowAddLocation] = useState(false);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    const getLocations = async () => {
      const locationsFromServer = await fetchLocations();
      setLocations(locationsFromServer);
    };

    getLocations();
  }, []);

  // const columns: ColDef[] = [
  //   { field: "id", headerName: "ID" },
  //   { field: "firstName", headerName: "First name", flex: 0.1 },
  //   { field: "lastName", headerName: "Last name", flex: 0.1 },
  //   {
  //     field: "age",
  //     headerName: "Age",
  //     type: "number"
  //   }
  //
  // ];
  //
  // const rows = [
  //   { id: 1, lastName: "Snow", firstName: "Jon", age: 35 },
  //   { id: 2, lastName: "Lannister", firstName: "Cersei", age: 42 },
  //   { id: 3, lastName: "Lannister", firstName: "Jaime", age: 45 },
  //   { id: 4, lastName: "Stark", firstName: "Arya", age: 16 },
  //   { id: 5, lastName: "Targaryen", firstName: "Daenerys", age: null }]

  // Fetch Locations
  const fetchLocations = async () => {
    const res = await fetch("http://localhost:3001/api/v1/locations");
    const data = await res.json();

    console.log({ data });
    let columns = [];
    let rows = [];
    // loc:
    //   createdAt: "2021-02-17T16:24:16.772Z"
    //   deletedAt: null
    //   id: 2
    //   name: "location 2"
    //   note: "note location 02"
    //   number: 102
    //   updatedAt: "2021-02-17T16:2
    let locKeys = Object.keys(data.locations.rows[0]);

    console.log({ locKeys });
    data.locations.rows.map(loc => {
      rows.push(loc);
      // switch(loc)
    });
    console.log({ rows });
    return data;
  };

  // Fetch Location
  const fetchLocation = async id => {
    const res = await fetch(`http://localhost:3001/api/v1/locations/${id}`);
    const data = await res.json();

    return data;
  };

  // Add Location
  const addLocation = async location => {
    const res = await fetch("http://localhost:3001/api/v1/locations/", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(location)
    });

    const data = await res.json();

    setLocations([...locations, data]);

    // const id = Math.floor(Math.random() * 10000) + 1
    // const newLocation = { id, ...location }
    // setLocations([...locations, newLocation])
  };

  // Delete Location
  const deleteLocation = async id => {
    const res = await fetch(`http://localhost:3001/api/v1/locations/${id}`, {
      method: "DELETE"
    });
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setLocations(locations.filter(location => location.id !== id))
      : alert("Error Deleting This Location");
  };

  // Toggle Reminder
  const toggleReminder = async id => {
    const locationToToggle = await fetchLocation(id);
    const updLocation = {
      ...locationToToggle,
      reminder: !locationToToggle.reminder
    };

    const res = await fetch(`http://localhost:3001/api/v1/locations/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(updLocation)
    });

    const data = await res.json();

    setLocations(
      locations.map(location =>
        location.id === id ? { ...location, reminder: data.reminder } : location
      )
    );
  };

  return <LocationTable locations={locations} />;
};

export default Location;
