import { useState, useEffect } from "react";
import LocationTable from "../components/LocationTable";

const Location = () => {
  const [showAddLocation, setShowAddLocation] = useState(false);
  const [tasks, setLocations] = useState([]);

  useEffect(() => {
    const getLocations = async () => {
      const tasksFromServer = await fetchLocations();
      setLocations(tasksFromServer);
    };

    getLocations();
  }, []);

  // Fetch Locations
  const fetchLocations = async () => {
    const res = await fetch("http://localhost:5000/tasks");
    const data = await res.json();

    return data;
  };

  // Fetch Location
  const fetchLocation = async id => {
    const res = await fetch(`http://localhost:5000/tasks/${id}`);
    const data = await res.json();

    return data;
  };

  // Add Location
  const addLocation = async task => {
    const res = await fetch("http://localhost:5000/tasks", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(task)
    });

    const data = await res.json();

    setLocations([...tasks, data]);

    // const id = Math.floor(Math.random() * 10000) + 1
    // const newLocation = { id, ...task }
    // setLocations([...tasks, newLocation])
  };

  // Delete Location
  const deleteLocation = async id => {
    const res = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "DELETE"
    });
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setLocations(tasks.filter(task => task.id !== id))
      : alert("Error Deleting This Location");
  };

  // Toggle Reminder
  const toggleReminder = async id => {
    const taskToToggle = await fetchLocation(id);
    const updLocation = { ...taskToToggle, reminder: !taskToToggle.reminder };

    const res = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(updLocation)
    });

    const data = await res.json();

    setLocations(
      tasks.map(task =>
        task.id === id ? { ...task, reminder: data.reminder } : task
      )
    );
  };

  return <LocationTable />;
};

export default Location;
