import * as React from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
// import MenuItem from "@material-ui/core/MenuItem";
import SelectTextField from "./SelectTextField";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      "& .MuiTextField-root": {
        margin: theme.spacing(1),
        width: "55ch"
      }
    }
  })
);

export default function ProductUpdateDialog({
  params,
  handleUpdate,
  handleClose,
  open,
  namings,
  decimalNumbers,
  locations,
  notes,
  employees
}) {
  const classes = useStyles();
  const [product, setProduct] = React.useState({});
  const [note, setNote] = React.useState(params.note);
  // const [naming, setNaming] = React.useState(params.naming);

  const handleChangeNote = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNote(event.target.value);
    setProduct(Object.assign(product, { note: event.target.value }));
  };

  // const handleChangeNaming = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   setNaming(event.target.value);
  //   setProduct(Object.assign(product, { naming: event.target.value }));
  // };

  return (
    <Dialog
      open={open}
      onClose={handleClose}
      aria-labelledby="form-dialog-title"
    >
      <DialogTitle id="form-dialog-title">Изделия</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Пожалуйста, отредактируйте нужные поля!
        </DialogContentText>
        <form className={classes.root} noValidate autoComplete="off">
          <SelectTextField
            items={namings}
            required={true}
            needId={true}
            value={namings.map(el => el.name === params.naming)}
            title="Наименование"
            getItem={item => {
              setProduct(Object.assign(product, { namingId: item }));
            }}
          />

          <TextField
            id="standard-multiline-note"
            label="Примечание"
            multiline
            rowsMax={4}
            value={note}
            onChange={handleChangeNote}
          />
        </form>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="primary">
          Отменить
        </Button>
        <Button onClick={ev => handleUpdate(ev, product)} color="primary">
          Редактировать
        </Button>
      </DialogActions>
    </Dialog>
  );
}
