import { useState, useEffect } from "react";
import { useSnackbar } from "notistack";
import Tooltip from "@material-ui/core/Tooltip";
import ProductTable from "../components/Product/ProductTable";
var moment = require("moment");

const Product = () => {
  const [products, setProducts] = useState({});
  const { enqueueSnackbar } = useSnackbar();

  useEffect(() => {
    const getProducts = async () => {
      const productsFromServer = await fetchProducts();
      setProducts(productsFromServer);
    };
    getProducts();
  }, []);

  // Fetch Products
  const fetchProducts = async () => {
    const res = await fetch("http://localhost:3001/api/v1/products");
    const data = await res.json();
    let columns = [];
    let rows = data.products.rows.map(row => {
      row.createdAt = moment(row.createdAt).format("YYYY.MM.DD HH:mm");
      row.updatedAt = moment(row.updatedAt).format("YYYY.MM.DD HH:mm");
      // if (row.type) row.type = row.type.name;
      if (row.naming) row.naming = row.naming.name;
      if (row.decimalNumber) row.decimalNumber = row.decimalNumber.name;
      if (row.location) row.location = row.location.name;
      if (row.note) row.note = row.note.name;
      if (row.employee) row.employee = row.employee.name;
      return row;
    });
    let locColumns = [];
    if (data.products.count > 0)
      locColumns = Object.keys(data.products.rows[0]);

    locColumns.forEach(item => {
      let obj = {};
      switch (item) {
        case "id":
          obj.headerName = "ID";
          obj.field = item;
          break;
        case "name":
          obj.headerName = "Название";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "number":
          obj.headerName = "Номер";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "decimalNumber":
          obj.headerName = "Децимальный номер";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "bookingDate":
          obj.headerName = "Дата бронирования";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "year":
          obj.headerName = "Год";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "location":
          obj.headerName = "Место производства";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "type":
          obj.headerName = "Тип изделия ";
          obj.field = item.number;
          obj.flex = 0.4;
          obj.renderCell = (params: CellParams) => (
            <Tooltip title={item.name}>{obj.field}</Tooltip>
          );
          break;
        case "serialNumber":
          obj.headerName = "Порядковый номер";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "employee":
          obj.headerName = "Сотрудник";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "description":
          obj.headerName = "Описание";
          obj.field = item;
          obj.flex = 0.4;
          break;
        case "createdAt":
          obj.headerName = "Дата создания";
          obj.field = item;
          obj.flex = 0.2;
          break;
        case "updatedAt":
          obj.headerName = "Дата редактирования";
          obj.field = item;
          obj.flex = 0.2;
          break;
        case "note":
          obj.headerName = "Примечание";
          obj.field = item;
          obj.flex = 0.4;
          break;
        default:
          break;
      }
      if (
        data.products.count > 0 &&
        item !== "deletedAt" &&
        item !== "typeId" &&
        item !== "namingId" &&
        item !== "locationId" &&
        item !== "noteId" &&
        item !== "employeeId"
      ) {
        columns.push(obj);
      }
    });

    let products = {};
    console.log({ columns });
    // move the type in 3 column left
    // columns.forEach((col, index) => {
    //   if (col.headerName === "Тип") {
    //     columns.splice(index, 1);
    //     columns.splice(index - 3, 0, col);
    //   }
    // });

    products.rows = rows;
    products.columns = columns;
    return products;
  };

  // Add Product
  const addProduct = async product => {
    try {
      const res = await fetch("http://localhost:3001/api/v1/products/", {
        method: "POST",
        body: JSON.stringify(product),
        headers: {
          "Content-type": "application/json"
        }
      });

      const data = await res.json();
      if (data["Bad Request"]) throw new Error(data["Bad Request"]);
      if (data["errorMessage"]) throw new Error(data["errorMessage"]);
      let newRows = [];
      data.product.type = data.product.type.name;
      if (products.columns.length > 0) {
        data.product.createdAt = moment(data.product.createdAt).format(
          "YYYY.MM.DD HH:mm"
        );
        data.product.updatedAt = moment(data.product.updatedAt).format(
          "YYYY.MM.DD HH:mm"
        );
        newRows = [...products.rows, data.product];
        let newProducts = Object.assign(
          {},
          { columns: products.columns, rows: newRows }
        );
        setProducts(newProducts);
      } else {
        const productsFromServer = await fetchProducts();
        setProducts(productsFromServer);
      }
    } catch (err) {
      console.log(err);
      enqueueSnackbar(`${err.message}`, {
        variant: "error",
        anchorOrigin: {
          vertical: "top",
          horizontal: "center"
        }
      });
    }
  };

  // Add Product
  const updateProduct = async product => {
    try {
      const res = await fetch(
        `http://localhost:3001/api/v1/products/${product.id}`,
        {
          method: "PUT",
          headers: {
            "Content-type": "application/json"
          },
          body: JSON.stringify(product)
        }
      );

      const data = await res.json();
      if (data["Bad Request"]) throw new Error(data["Bad Request"]);
      if (data["errorMessage"]) throw new Error(data["errorMessage"]);
      data.product.createdAt = moment(data.product.createdAt).format(
        "YYYY.MM.DD HH:mm"
      );
      data.product.updatedAt = moment(data.product.updatedAt).format(
        "YYYY.MM.DD HH:mm"
      );
      let newRows = [];
      data.product.type = data.product.type.name;
      if (products.columns.length > 0) {
        newRows = products.rows.map(loc =>
          loc.id === product.id ? data.product : loc
        );
        let newProducts = Object.assign(
          {},
          { columns: products.columns, rows: newRows }
        );
        setProducts(newProducts);
      } else {
        const productsFromServer = await fetchProducts();
        setProducts(productsFromServer);
      }
    } catch (err) {
      console.log(err);
      enqueueSnackbar(`${err.message}`, {
        variant: "error",
        anchorOrigin: {
          vertical: "top",
          horizontal: "center"
        }
      });
    }
  };

  // Delete Product
  const deleteProduct = async parameters => {
    try {
      await fetch(`http://localhost:3001/api/v1/products/${parameters.id}`, {
        method: "DELETE"
      });

      let newRows = products.rows.filter(row => row.id !== parameters.id);
      let newProducts = Object.assign(
        {},
        { columns: products.columns, rows: newRows }
      );
      setProducts(newProducts);
    } catch (err) {
      console.log(err);
      enqueueSnackbar(`${err.message}`, {
        variant: "error",
        anchorOrigin: {
          vertical: "top",
          horizontal: "center"
        }
      });
    }
  };

  return (
    <ProductTable
      products={products}
      deleteProduct={deleteProduct}
      addProduct={addProduct}
      updateProduct={updateProduct}
    ></ProductTable>
  );
};

export default Product;
